import React, { useState, useEffect } from 'react';
import './App.css';

interface AttendanceRecord {
  id: string;
  timestamp: string;
  status: string;
}

function App(): JSX.Element {
  const [records, setRecords] = useState<AttendanceRecord[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    // Simulate automatic attendance check
    const checkAttendance = () => {
      const currentTime = new Date();
      const newRecord: AttendanceRecord = {
        id: Math.random().toString(36).substr(2, 9),
        timestamp: currentTime.toISOString(),
        status: 'Present'
      };
      
      setRecords((prev: AttendanceRecord[]) => [...prev, newRecord]);
      setLoading(false);
    };

    // Check attendance every 5 seconds
    const interval = setInterval(checkAttendance, 5000);
    checkAttendance(); // Initial check

    return () => clearInterval(interval);
  }, []);

  if (loading) {
    return (
      <div className="App">
        <div className="loading">Loading attendance system...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="App">
        <div className="error">{error}</div>
      </div>
    );
  }

  return (
    <div className="App">
      <header className="App-header">
        <h1>AI Attendance System</h1>
        <div className="status-container">
          <h2>Automatic Attendance</h2>
          <div className="status-indicator active">
            System Active
          </div>
        </div>
        <div className="records-container">
          <h3>Recent Records</h3>
          <div className="records-list">
            {records.map((record: AttendanceRecord) => (
              <div key={record.id} className="record-item">
                <span className="record-time">
                  {new Date(record.timestamp).toLocaleTimeString()}
                </span>
                <span className="record-status">
                  {record.status}
                </span>
              </div>
            ))}
          </div>
        </div>
      </header>
    </div>
  );
}

export default App;
