import React, { useState, useEffect } from 'react';
import './App.css';

interface AttendanceRecord {
  id: string;
  timestamp: string;
  status: string;
  userId: string;
  cardId: string;
  userName?: string;
  department?: string;
}

interface User {
  userId: string;
  cardId: string;
  name: string;
  department: string;
  password: string;
}

// Simulated user database
const MOCK_USERS: { [key: string]: User } = {
  '12345678': {
    userId: '12345678',
    cardId: 'CARD001',
    name: 'John Doe',
    department: 'Computer Science',
    password: '1234'
  },
  '87654321': {
    userId: '87654321',
    cardId: 'CARD002',
    name: 'Jane Smith',
    department: 'Engineering',
    password: '4321'
  }
};

function App(): JSX.Element {
  const [records, setRecords] = useState<AttendanceRecord[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);
  const [user, setUser] = useState<User | null>(null);
  const [cardInput, setCardInput] = useState('');
  const [password, setPassword] = useState('');
  const [scanning, setScanning] = useState(false);
  const [showPassword, setShowPassword] = useState(false);
  const [isLocked, setIsLocked] = useState(true);
  const [foundUser, setFoundUser] = useState<User | null>(null);

  useEffect(() => {
    if (user) {
      const fetchRecords = async () => {
        try {
          const response = await fetch('http://localhost:3001/api/attendance');
          const data = await response.json();
          setRecords(data);
          setLoading(false);
        } catch (err) {
          console.error('Error fetching records:', err);
          setError('Failed to fetch attendance records');
          setLoading(false);
        }
      };

      const interval = setInterval(fetchRecords, 5000);
      fetchRecords();

      return () => clearInterval(interval);
    }
  }, [user]);

  const simulateCardScan = () => {
    setScanning(true);
    // Simulate card scanning process
    setTimeout(() => {
      const randomUser = Object.values(MOCK_USERS)[Math.floor(Math.random() * Object.values(MOCK_USERS).length)];
      setFoundUser(randomUser);
      setShowPassword(true);
      setScanning(false);
    }, 1500);
  };

  const handleManualLogin = (e: React.FormEvent) => {
    e.preventDefault();
    const user = MOCK_USERS[cardInput];
    if (user) {
      setFoundUser(user);
      setShowPassword(true);
    } else {
      setError('Invalid card or user ID');
    }
  };

  const handlePasswordSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (foundUser && password === foundUser.password) {
      setIsLocked(false);
      // Simulate unlock animation
      setTimeout(() => {
        handleLogin(foundUser);
      }, 1000);
    } else {
      setError('Invalid password');
      setPassword('');
    }
  };

  const handleLogin = (user: User) => {
    setUser(user);
    setError(null);
    recordAttendance(user);
    // Reset states
    setFoundUser(null);
    setPassword('');
    setShowPassword(false);
    setIsLocked(true);
  };

  const recordAttendance = async (user: User) => {
    try {
      const response = await fetch('http://localhost:3001/api/attendance', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          timestamp: new Date().toISOString(),
          status: 'Present',
          userId: user.userId,
          cardId: user.cardId,
          userName: user.name,
          department: user.department
        }),
      });

      if (!response.ok) {
        throw new Error('Failed to record attendance');
      }

      const newRecord = await response.json();
      setRecords(prev => [newRecord, ...prev]);
    } catch (err) {
      console.error('Error recording attendance:', err);
      setError('Failed to record attendance');
    }
  };

  const handleLogout = () => {
    setUser(null);
    setRecords([]);
    setCardInput('');
    setPassword('');
    setShowPassword(false);
    setIsLocked(true);
  };

  if (!user) {
    return (
      <div className="App">
        <header className="App-header">
          <h1>AI Attendance System</h1>
          <div className="login-container">
            <h2>Card Scanner</h2>
            {error && <div className="error">{error}</div>}
            <div className="scanner-container">
              <button 
                onClick={simulateCardScan} 
                className={`scan-button ${scanning ? 'scanning' : ''}`}
                disabled={scanning || showPassword}
              >
                {scanning ? 'Scanning...' : 'Touch Card Here'}
              </button>
            </div>
            {showPassword ? (
              <div className="password-section">
                <div className="lock-container">
                  <span className={`lock-icon ${isLocked ? 'locked' : 'unlocked'}`}>
                    {isLocked ? 'ðŸ”’' : 'ðŸ”“'}
                  </span>
                </div>
                <form onSubmit={handlePasswordSubmit} className="password-form">
                  <input
                    type="password"
                    placeholder="Enter Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    className="password-input"
                    autoFocus
                  />
                  <button type="submit" className="login-button">
                    Verify
                  </button>
                </form>
              </div>
            ) : (
              <div className="manual-login">
                <h3>Or Enter Card/ID Manually</h3>
                <form onSubmit={handleManualLogin} className="login-form">
                  <input
                    type="text"
                    placeholder="Enter Card ID or User ID"
                    value={cardInput}
                    onChange={(e) => setCardInput(e.target.value)}
                    className="login-input"
                    maxLength={8}
                  />
                  <button type="submit" className="login-button">
                    Submit
                  </button>
                </form>
              </div>
            )}
          </div>
        </header>
      </div>
    );
  }

  if (loading) {
    return (
      <div className="App">
        <div className="loading">Loading attendance system...</div>
      </div>
    );
  }

  return (
    <div className="App">
      <header className="App-header">
        <div className="header-content">
          <h1>AI Attendance System</h1>
          <div className="user-info">
            <div className="user-details">
              <div>ID: {user.userId}</div>
              <div>Name: {user.name}</div>
              <div>Dept: {user.department}</div>
            </div>
            <button onClick={handleLogout} className="logout-button">
              Logout
            </button>
          </div>
        </div>
        <div className="status-container">
          <h2>Automatic Attendance</h2>
          <div className="status-indicator active">
            System Active
          </div>
          <button onClick={() => recordAttendance(user)} className="record-button">
            Record Attendance
          </button>
        </div>
        <div className="records-container">
          <h3>Recent Records</h3>
          <div className="records-list">
            {records.map((record: AttendanceRecord) => (
              <div key={record.id} className="record-item">
                <div className="record-details">
                  <span className="record-time">
                    {new Date(record.timestamp).toLocaleTimeString()}
                  </span>
                  <span className="record-user">
                    {record.userName || record.userId}
                  </span>
                  <span className="record-department">
                    {record.department}
                  </span>
                </div>
                <span className="record-status">
                  {record.status}
                </span>
              </div>
            ))}
          </div>
        </div>
      </header>
    </div>
  );
}

export default App;
