import React, { useState, useEffect } from 'react';
import './App.css';

interface AttendanceRecord {
  id: string;
  timestamp: string;
  status: string;
}

interface User {
  email: string;
  name: string;
}

function App(): JSX.Element {
  const [records, setRecords] = useState<AttendanceRecord[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);
  const [user, setUser] = useState<User | null>(null);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  useEffect(() => {
    if (user) {
      const fetchRecords = async () => {
        try {
          const response = await fetch('http://localhost:3001/api/attendance');
          const data = await response.json();
          setRecords(data);
          setLoading(false);
        } catch (err) {
          console.error('Error fetching records:', err);
          setError('Failed to fetch attendance records');
          setLoading(false);
        }
      };

      // Check attendance every 5 seconds
      const interval = setInterval(fetchRecords, 5000);
      fetchRecords(); // Initial fetch

      return () => clearInterval(interval);
    }
  }, [user]);

  const handleLogin = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      // For demo purposes, simple validation
      if (email && password) {
        setUser({ email, name: email.split('@')[0] });
        setError(null);
      } else {
        setError('Please enter both email and password');
      }
    } catch (err) {
      console.error('Login error:', err);
      setError('Failed to login');
    }
  };

  const handleLogout = () => {
    setUser(null);
    setRecords([]);
    setEmail('');
    setPassword('');
  };

  if (!user) {
    return (
      <div className="App">
        <header className="App-header">
          <h1>AI Attendance System</h1>
          <div className="login-container">
            <h2>Login</h2>
            {error && <div className="error">{error}</div>}
            <form onSubmit={handleLogin} className="login-form">
              <input
                type="email"
                placeholder="Email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                className="login-input"
              />
              <input
                type="password"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                className="login-input"
              />
              <button type="submit" className="login-button">
                Login
              </button>
            </form>
          </div>
        </header>
      </div>
    );
  }

  if (loading) {
    return (
      <div className="App">
        <div className="loading">Loading attendance system...</div>
      </div>
    );
  }

  return (
    <div className="App">
      <header className="App-header">
        <div className="header-content">
          <h1>AI Attendance System</h1>
          <div className="user-info">
            <span>Welcome, {user.name}</span>
            <button onClick={handleLogout} className="logout-button">
              Logout
            </button>
          </div>
        </div>
        <div className="status-container">
          <h2>Automatic Attendance</h2>
          <div className="status-indicator active">
            System Active
          </div>
          <button onClick={() => {}} className="record-button">
            Record Attendance
          </button>
        </div>
        <div className="records-container">
          <h3>Recent Records</h3>
          <div className="records-list">
            {records.map((record: AttendanceRecord) => (
              <div key={record.id} className="record-item">
                <span className="record-time">
                  {new Date(record.timestamp).toLocaleTimeString()}
                </span>
                <span className="record-status">
                  {record.status}
                </span>
              </div>
            ))}
          </div>
        </div>
      </header>
    </div>
  );
}

export default App;
